package iteration_case;
// Downloaded from https://github.com/KuKapoor02
// Visit https://www.ourcoaching.com/
public class myClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		
		// using for loop
		System.out.println("for loop execution");
		for(i=0;i<10;i++) {
			System.out.println("i = "+i);
		}
		
		// Using while loop
		System.out.println("while loop execution");
		i=0;
		while(i<10) {
			System.out.println("i = "+i);
			i++;
		}
		
		// Using do while loop
				System.out.println("do while loop execution");
				i=0;
				do{
					System.out.println("i = "+i);
					i++;
				}while(i<10); 

	}

}
